import React, { useState, useEffect } from "react";
import { PublicClientApplication } from "@azure/msal-browser";
import { MsalProvider, useMsal, useIsAuthenticated } from "@azure/msal-react";

const msalConfig = {
  auth: {
    clientId: "5c1e64c0-76f2-4200-8ee5-b3b3d19b53da",
    authority: "https://login.microsoftonline.com/common",
    redirectUri: window.location.origin,
  },
};
const msalInstance = new PublicClientApplication(msalConfig);

const loginRequestOutlook = {
  scopes: ["openid", "profile", "User.Read", "Mail.Read"],
  prompt: "select_account"
};
const loginRequestSharePoint = {
  scopes: ["openid", "profile", "Sites.ReadWrite.All"],
  prompt: "select_account"
};

function DualLogin({
  useSameAccount,
  setUseSameAccount,
  outlookToken,
  setOutlookToken,
  sharepointToken,
  setSharepointToken,
  setOutlookAccount,
  setSharepointAccount,
}) {
  const { instance } = useMsal();
  const [loginInProgress, setLoginInProgress] = useState(false);

  const handleLoginOutlook = async () => {
    if (loginInProgress) return;
    setLoginInProgress(true);
    try {
      const res = await instance.loginPopup(loginRequestOutlook);
      setOutlookToken(res.accessToken);
      setOutlookAccount(res.account);
    } finally {
      setLoginInProgress(false);
    }
  };
  const handleLoginSharePoint = async () => {
    if (loginInProgress) return;
    setLoginInProgress(true);
    try {
      const res = await instance.loginPopup(loginRequestSharePoint);
      setSharepointToken(res.accessToken);
      setSharepointAccount(res.account);
    } finally {
      setLoginInProgress(false);
    }
  };
  const handleLogout = () => {
    instance.logoutPopup();
    setOutlookToken(null);
    setSharepointToken(null);
    setOutlookAccount(null);
    setSharepointAccount(null);
  };
  return (
    <div style={{ marginBottom: 20 }}>
      <label>
        <input
          type="checkbox"
          checked={useSameAccount}
          onChange={e => setUseSameAccount(e.target.checked)}
        />
        Use the same account for both Outlook and SharePoint
      </label>
      <div style={{ marginTop: 10 }}>
        {useSameAccount ? (
          <button onClick={handleLoginOutlook} disabled={loginInProgress}>Login with Microsoft</button>
        ) : (
          <>
            <button onClick={handleLoginOutlook} style={{ marginRight: 8 }} disabled={loginInProgress}>
              Login to Outlook
            </button>
            <button onClick={handleLoginSharePoint} disabled={loginInProgress}>Login to SharePoint</button>
          </>
        )}
        <button onClick={handleLogout} style={{ marginLeft: 16 }} disabled={loginInProgress}>Logout</button>
      </div>
    </div>
  );
}

function EmailList({ token, selectedEmails, setSelectedEmails, emails }) {
  const [loading, setLoading] = useState(false);
  const [fetchedEmails, setFetchedEmails] = useState([]);

  useEffect(() => {
    if (!token) return;
    setLoading(true);
    fetch("https://graph.microsoft.com/v1.0/me/mailFolders/inbox/messages?$top=20", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        setFetchedEmails(data.value || []);
        setLoading(false);
      });
  }, [token]);

  // Use fetchedEmails if emails prop is not provided (for backward compatibility)
  const emailList = emails && emails.length > 0 ? emails : fetchedEmails;

  const toggleEmail = (id) => {
    setSelectedEmails((prev) =>
      prev.includes(id) ? prev.filter((eid) => eid !== id) : [...prev, id]
    );
  };

  return (
    <div>
      <h3>Select Emails</h3>
      {loading && <div>Loading emails...</div>}
      <ul style={{ maxHeight: 300, overflowY: "auto", padding: 0 }}>
        {emailList.map((email) => (
          <li key={email.id} style={{ listStyle: "none", marginBottom: 8 }}>
            <label>
              <input
                type="checkbox"
                checked={selectedEmails.includes(email.id)}
                onChange={() => toggleEmail(email.id)}
              />
              <b>{email.subject || "(No Subject)"}</b> — {email.from?.emailAddress?.address}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
}

function FieldMapping({ columns, fieldMapping, setFieldMapping }) {
  // Only show columns that are not hidden and are not system fields
  const selectableColumns = columns.filter(col => !col.hidden && !col.readOnly && col.name !== 'ContentType' && col.name !== 'Attachments');
  const appFields = ['subject', 'description', 'user', 'ticketnumber', 'assigned to'];
  return (
    <div style={{ margin: '20px 0', padding: 10, border: '1px solid #ccc', borderRadius: 6 }}>
      <h4>SharePoint Field Mapping</h4>
      <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
        {appFields.map(appField => (
          <label key={appField}>
            {appField.charAt(0).toUpperCase() + appField.slice(1)}:
            <select
              value={fieldMapping[appField] || ''}
              onChange={e => setFieldMapping(f => ({ ...f, [appField]: e.target.value }))}
              style={{ marginLeft: 8 }}
            >
              <option value="">(Do not map)</option>
              {selectableColumns.map(col => (
                <option key={col.name} value={col.name}>
                  {col.displayName} ({col.name})
                </option>
              ))}
            </select>
          </label>
        ))}
      </div>
    </div>
  );
}

function SharePointSelector({ token, siteId, setSiteId, listId, setListId, columns, setColumns }) {
  const [sites, setSites] = useState([]);
  const [lists, setLists] = useState([]);

  useEffect(() => {
    if (!token) return;
    fetch("https://graph.microsoft.com/v1.0/sites?search=*", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setSites(data.value || []));
  }, [token]);

  useEffect(() => {
    if (!token || !siteId) return;
    fetch(`https://graph.microsoft.com/v1.0/sites/${siteId}/lists`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setLists(data.value || []));
  }, [token, siteId]);

  useEffect(() => {
    if (!token || !siteId || !listId) {
      setColumns([]);
      return;
    }
    fetch(`https://graph.microsoft.com/v1.0/sites/${siteId}/lists/${listId}/columns`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setColumns(data.value || []));
  }, [token, siteId, listId, setColumns]);

  return (
    <div>
      <div>
        <label>
          SharePoint Site:
          <select value={siteId} onChange={e => setSiteId(e.target.value)}>
            <option value="">Select a site</option>
            {sites.map((site) => (
              <option key={site.id} value={site.id}>
                {site.displayName}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          SharePoint List:
          <select value={listId} onChange={e => setListId(e.target.value)}>
            <option value="">Select a list</option>
            {lists.map((list) => (
              <option key={list.id} value={list.id}>
                {list.displayName}
              </option>
            ))}
          </select>
        </label>
      </div>
    </div>
  );
}

function CreateTicketsButton({ token, selectedEmails, emails, siteId, listId, onResult, fieldMapping }) {
  const [loading, setLoading] = useState(false);

  const handleCreateTickets = async () => {
    if (!siteId || !listId || selectedEmails.length === 0) {
      alert("Please select emails, a site, and a list.");
      return;
    }
    setLoading(true);
    let results = [];
    for (let emailId of selectedEmails) {
      const email = emails.find((e) => e.id === emailId);
      if (!email) continue;
      // Format ticket number as YYYYMMDDHHmm from receivedDateTime (local, 24-hour/military time)
      let ticketNumber = "";
      if (email.receivedDateTime) {
        const dt = new Date(email.receivedDateTime);
        console.log('TicketNumber Debug:', email.receivedDateTime, dt.toString());
        const pad = (n) => n.toString().padStart(2, '0');
        ticketNumber = `${dt.getFullYear()}${pad(dt.getMonth()+1)}${pad(dt.getDate())}${pad(dt.getHours())}${pad(dt.getMinutes())}`;
      }
      const payload = {
        fields: {
          ...(fieldMapping.subject ? { [fieldMapping.subject]: email.subject } : {}),
          ...(fieldMapping.description ? { [fieldMapping.description]: email.bodyPreview } : {}),
          ...(fieldMapping.user ? { [fieldMapping.user]: email.from?.emailAddress?.address } : {}),
          ...(fieldMapping.ticketnumber ? { [fieldMapping.ticketnumber]: ticketNumber } : {}),
        },
      };
      console.log('SharePoint payload:', payload);
      try {
        const res = await fetch(
          `https://graph.microsoft.com/v1.0/sites/${siteId}/lists/${listId}/items`,
          {
            method: "POST",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
            body: JSON.stringify(payload),
          }
        );
        if (res.ok) {
          results.push({ emailId, status: "success" });
        } else {
          results.push({ emailId, status: "error", error: await res.text() });
        }
      } catch (err) {
        results.push({ emailId, status: "error", error: err.message });
      }
    }
    setLoading(false);
    onResult(results);
  };

  return (
    <div>
      <button onClick={handleCreateTickets} disabled={loading}>
        {loading ? "Creating..." : "Create Tickets"}
      </button>
    </div>
  );
}

function TicketSyncPage({ outlookToken, sharepointToken, useSameAccount }) {
  const [selectedEmails, setSelectedEmails] = useState([]);
  const [emails, setEmails] = useState([]);
  const [siteId, setSiteId] = useState("");
  const [listId, setListId] = useState("");
  const [columns, setColumns] = useState([]);
  const [fieldMapping, setFieldMapping] = useState({});
  const [results, setResults] = useState([]);

  // Token to use for SharePoint
  const spToken = useSameAccount ? outlookToken : sharepointToken;

  // Fetch emails after Outlook login
  useEffect(() => {
    if (!outlookToken) return;
    fetch("https://graph.microsoft.com/v1.0/me/mailFolders/inbox/messages?$top=20", {
      headers: { Authorization: `Bearer ${outlookToken}` },
    })
      .then((res) => res.json())
      .then((data) => setEmails(data.value || []));
  }, [outlookToken]);

  return (
    <div>
      <h2>Ticket Sync</h2>
      <SharePointSelector
        token={spToken}
        siteId={siteId}
        setSiteId={setSiteId}
        listId={listId}
        setListId={setListId}
        columns={columns}
        setColumns={setColumns}
      />
      {columns.length > 0 && (
        <FieldMapping
          columns={columns}
          fieldMapping={fieldMapping}
          setFieldMapping={setFieldMapping}
        />
      )}
      <EmailList
        token={outlookToken}
        selectedEmails={selectedEmails}
        setSelectedEmails={setSelectedEmails}
        emails={emails}
      />
      <CreateTicketsButton
        token={spToken}
        selectedEmails={selectedEmails}
        emails={emails}
        siteId={siteId}
        listId={listId}
        onResult={setResults}
        fieldMapping={fieldMapping}
      />
      <div style={{ marginTop: 20 }}>
        {results.length > 0 && (
          <div>
            <h4>Results:</h4>
            <ul>
              {results.map((r, i) => (
                <li key={i}>
                  Email ID: {r.emailId} — {r.status}
                  {r.error && <span style={{ color: "red" }}> ({r.error})</span>}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

function ResponseGeneratorPage({ outlookToken, sharepointToken, useSameAccount }) {
  const [selectedEmails, setSelectedEmails] = useState([]);
  const [emails, setEmails] = useState([]);
  const [generatedResponse, setGeneratedResponse] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [editedResponse, setEditedResponse] = useState("");
  const [ccRecipients, setCcRecipients] = useState([]);
  const [newCcEmail, setNewCcEmail] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);

  // Fetch emails after Outlook login
  useEffect(() => {
    if (!outlookToken) return;
    fetch("https://graph.microsoft.com/v1.0/me/mailFolders/inbox/messages?$top=20", {
      headers: { Authorization: `Bearer ${outlookToken}` },
    })
      .then((res) => res.json())
      .then((data) => setEmails(data.value || []));
  }, [outlookToken]);

  // Simple template matching based on email content
  const generateResponse = async (email) => {
    setIsGenerating(true);
    const subject = email.subject?.toLowerCase() || "";
    const body = email.bodyPreview?.toLowerCase() || "";
    const content = `${subject} ${body}`;

    // Simple keyword-based template matching
    let template = "";
    if (content.includes("password") || content.includes("reset")) {
      template = `Hi ${email.from?.emailAddress?.name || "there"},

I can help you with your password reset request. Please follow these steps:

1. Go to https://portal.office.com
2. Click "Can't access your account?"
3. Follow the password reset process

If you continue to have issues, please let me know and I'll assist further.

Best regards,
IT Support`;
    } else if (content.includes("software") || content.includes("install")) {
      template = `Hi ${email.from?.emailAddress?.name || "there"},

I understand you need help with software installation. Here's what I can do:

1. I'll schedule a remote session to assist with the installation
2. Please let me know your availability for the next 24 hours
3. I'll send you a Teams meeting invite

Best regards,
IT Support`;
    } else {
      template = `Hi ${email.from?.emailAddress?.name || "there"},

Thank you for contacting IT Support. I've received your request and will assist you shortly.

Ticket Details:
- Subject: ${email.subject}
- Priority: Normal
- Assigned to: IT Support Team

I'll update you on the progress within the next few hours.

Best regards,
IT Support`;
    }

    setGeneratedResponse(template);
    setEditedResponse(template);
    setIsGenerating(false);
  };

  const handleGenerateResponse = async () => {
    if (selectedEmails.length === 0) {
      alert("Please select at least one email to generate a response.");
      return;
    }

    const email = emails.find(e => e.id === selectedEmails[0]);
    if (email) {
      await generateResponse(email);
      setIsEditing(true);
    }
  };

  const handleSendResponse = async () => {
    if (!editedResponse.trim()) {
      alert("Please enter a response before sending.");
      return;
    }

    // Here you would integrate with Microsoft Graph API to send emails
    console.log('Sending response:', {
      response: editedResponse,
      ccRecipients: ccRecipients,
      emailIds: selectedEmails
    });

    // For now, just show success message
    alert(`Response sent to ${selectedEmails.length} email(s) with ${ccRecipients.length} CC recipient(s)`);

    // Reset form
    setGeneratedResponse("");
    setEditedResponse("");
    setCcRecipients([]);
    setIsEditing(false);
  };

  const addCcRecipient = () => {
    if (newCcEmail && newCcEmail.includes("@")) {
      setCcRecipients([...ccRecipients, newCcEmail]);
      setNewCcEmail("");
    }
  };

  const removeCcRecipient = (email) => {
    setCcRecipients(ccRecipients.filter(e => e !== email));
  };

  return (
    <div>
      <h2>Response Generator</h2>

      <EmailList
        token={outlookToken}
        selectedEmails={selectedEmails}
        setSelectedEmails={setSelectedEmails}
        emails={emails}
      />

      <div style={{ margin: '20px 0', padding: 10, border: '1px solid #ccc', borderRadius: 6 }}>
        <h4>Generate Response</h4>

        {!isEditing && (
          <button onClick={handleGenerateResponse} disabled={isGenerating || selectedEmails.length === 0}>
            {isGenerating ? "Generating..." : "Generate Response"}
          </button>
        )}

        {isEditing && (
          <div>
            <div style={{ marginBottom: 10 }}>
              <label>
                <strong>Edit Response:</strong>
                <textarea
                  value={editedResponse}
                  onChange={(e) => setEditedResponse(e.target.value)}
                  style={{ width: '100%', height: '150px', marginTop: 5 }}
                  placeholder="Edit the generated response..."
                />
              </label>
            </div>

            <div style={{ marginBottom: 10 }}>
              <label>
                <strong>CC Recipients:</strong>
                <div style={{ marginTop: 5 }}>
                  {ccRecipients.map((email, index) => (
                    <span key={index} style={{ marginRight: 10, padding: '2px 8px', backgroundColor: '#f0f0f0', borderRadius: 3 }}>
                      {email}
                      <button onClick={() => removeCcRecipient(email)} style={{ marginLeft: 5, border: 'none', background: 'none', color: 'red', cursor: 'pointer' }}>×</button>
                    </span>
                  ))}
                </div>
                <div style={{ marginTop: 5 }}>
                  <input
                    type="email"
                    value={newCcEmail}
                    onChange={(e) => setNewCcEmail(e.target.value)}
                    placeholder="Enter email address"
                    style={{ marginRight: 5 }}
                  />
                  <button onClick={addCcRecipient}>Add CC</button>
                </div>
              </label>
            </div>

            <div>
              <button onClick={handleSendResponse} style={{ marginRight: 10, backgroundColor: '#4CAF50', color: 'white', border: 'none', padding: '8px 16px', borderRadius: 4 }}>
                Send Response
              </button>
              <button onClick={() => setIsEditing(false)} style={{ backgroundColor: '#f44336', color: 'white', border: 'none', padding: '8px 16px', borderRadius: 4 }}>
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

