import React, { useState, useEffect } from "react";
import { PublicClientApplication } from "@azure/msal-browser";
import { MsalProvider, useMsal, useIsAuthenticated } from "@azure/msal-react";

const msalConfig = {
  auth: {
    clientId: "5c1e64c0-76f2-4200-8ee5-b3b3d19b53da",
    authority: "https://login.microsoftonline.com/common",
    redirectUri: window.location.origin,
  },
};
const loginRequest = {
  scopes: [
    "openid",
    "profile",
    "User.Read",
    "Mail.Read",
    "Sites.ReadWrite.All"
  ],
};

const msalInstance = new PublicClientApplication(msalConfig);

function LoginButton() {
  const { instance } = useMsal();
  const isAuthenticated = useIsAuthenticated();

  const handleLogin = () => {
    instance.loginPopup(loginRequest);
  };

  const handleLogout = () => {
    instance.logoutPopup();
  };

  return (
    <div style={{ marginBottom: 20 }}>
      {!isAuthenticated ? (
        <button onClick={handleLogin}>Login with Microsoft</button>
      ) : (
        <button onClick={handleLogout}>Logout</button>
      )}
    </div>
  );
}

function EmailList({ token, selectedEmails, setSelectedEmails }) {
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!token) return;
    setLoading(true);
    fetch("https://graph.microsoft.com/v1.0/me/mailFolders/inbox/messages?$top=20", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        setEmails(data.value || []);
        setLoading(false);
      });
  }, [token]);

  const toggleEmail = (id) => {
    setSelectedEmails((prev) =>
      prev.includes(id) ? prev.filter((eid) => eid !== id) : [...prev, id]
    );
  };

  return (
    <div>
      <h3>Select Emails</h3>
      {loading && <div>Loading emails...</div>}
      <ul style={{ maxHeight: 300, overflowY: "auto", padding: 0 }}>
        {emails.map((email) => (
          <li key={email.id} style={{ listStyle: "none", marginBottom: 8 }}>
            <label>
              <input
                type="checkbox"
                checked={selectedEmails.includes(email.id)}
                onChange={() => toggleEmail(email.id)}
              />
              <b>{email.subject || "(No Subject)"}</b> — {email.from?.emailAddress?.address}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
}

function SharePointSelector({ token, siteId, setSiteId, listId, setListId }) {
  const [sites, setSites] = useState([]);
  const [lists, setLists] = useState([]);

  useEffect(() => {
    if (!token) return;
    fetch("https://graph.microsoft.com/v1.0/sites?search=*", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setSites(data.value || []));
  }, [token]);

  useEffect(() => {
    if (!token || !siteId) return;
    fetch(`https://graph.microsoft.com/v1.0/sites/${siteId}/lists`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setLists(data.value || []));
  }, [token, siteId]);

  return (
    <div>
      <div>
        <label>
          SharePoint Site:
          <select value={siteId} onChange={(e) => setSiteId(e.target.value)}>
            <option value="">Select a site</option>
            {sites.map((site) => (
              <option key={site.id} value={site.id}>
                {site.displayName}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          SharePoint List:
          <select value={listId} onChange={(e) => setListId(e.target.value)}>
            <option value="">Select a list</option>
            {lists.map((list) => (
              <option key={list.id} value={list.id}>
                {list.displayName}
              </option>
            ))}
          </select>
        </label>
      </div>
    </div>
  );
}

function FieldMapping({ fieldMapping, setFieldMapping }) {
  return (
    <div style={{ margin: '20px 0', padding: 10, border: '1px solid #ccc', borderRadius: 6 }}>
      <h4>SharePoint Field Mapping</h4>
      <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
        <label>
          Subject field:
          <input
            type="text"
            value={fieldMapping.subject}
            onChange={e => setFieldMapping(f => ({ ...f, subject: e.target.value }))}
            style={{ marginLeft: 8 }}
          />
        </label>
        <label>
          Description field:
          <input
            type="text"
            value={fieldMapping.description}
            onChange={e => setFieldMapping(f => ({ ...f, description: e.target.value }))}
            style={{ marginLeft: 8 }}
          />
        </label>
        <label>
          User field:
          <input
            type="text"
            value={fieldMapping.user}
            onChange={e => setFieldMapping(f => ({ ...f, user: e.target.value }))}
            style={{ marginLeft: 8 }}
          />
        </label>
        <label>
          Ticket Number field:
          <input
            type="text"
            value={fieldMapping.ticketnumber}
            onChange={e => setFieldMapping(f => ({ ...f, ticketnumber: e.target.value }))}
            style={{ marginLeft: 8 }}
          />
        </label>
      </div>
    </div>
  );
}

function CreateTicketsButton({ token, selectedEmails, emails, siteId, listId, onResult, fieldMapping }) {
  const [loading, setLoading] = useState(false);

  const handleCreateTickets = async () => {
    if (!siteId || !listId || selectedEmails.length === 0) {
      alert("Please select emails, a site, and a list.");
      return;
    }
    setLoading(true);
    let results = [];
    for (let emailId of selectedEmails) {
      const email = emails.find((e) => e.id === emailId);
      if (!email) continue;
      // Format ticket number as YYYYMMDDHHmm from receivedDateTime
      let ticketNumber = "";
      if (email.receivedDateTime) {
        const dt = new Date(email.receivedDateTime);
        const pad = (n) => n.toString().padStart(2, '0');
        ticketNumber = `${dt.getFullYear()}${pad(dt.getMonth()+1)}${pad(dt.getDate())}${pad(dt.getHours())}${pad(dt.getMinutes())}`;
      }
      const payload = {
        fields: {
          [fieldMapping.subject]: email.subject,
          [fieldMapping.description]: email.bodyPreview,
          [fieldMapping.user]: email.from?.emailAddress?.address,
          [fieldMapping.ticketnumber]: ticketNumber,
          // Add more fields as needed
        },
      };
      console.log('SharePoint payload:', payload);
      try {
        const res = await fetch(
          `https://graph.microsoft.com/v1.0/sites/${siteId}/lists/${listId}/items`,
          {
            method: 'POST',
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
          }
        );
        if (res.ok) {
          results.push(await res.json());
        }
      } catch (error) {
        console.error('Error creating ticket:', error);
      }
    }
    setLoading(false);
    onResult(results);
  };

  return (
    <div style={{ marginTop: 20 }}>
      <button onClick={handleCreateTickets} disabled={loading}>
        {loading ? 'Creating tickets...' : 'Create Tickets'}
      </button>
    </div>
  );
}

