import React, { useState, useEffect } from "react";
import { PublicClientApplication } from "@azure/msal-browser";
import { MsalProvider, useMsal, useIsAuthenticated } from "@azure/msal-react";

const msalConfig = {
  auth: {
    clientId: "5c1e64c0-76f2-4200-8ee5-b3b3d19b53da",
    authority: "https://login.microsoftonline.com/common",
    redirectUri: window.location.origin,
  },
  cache: {
    cacheLocation: "localStorage", // persist across tabs and refreshes
    storeAuthStateInCookie: false,
  },
};
const msalInstance = new PublicClientApplication(msalConfig);

const loginRequestOutlook = {
  scopes: ["openid", "profile", "User.Read", "Mail.Read"],
  prompt: "select_account"
};
const loginRequestSharePoint = {
  scopes: ["openid", "profile", "Sites.ReadWrite.All"],
  prompt: "select_account"
};

function DualLogin({
  useSameAccount,
  setUseSameAccount,
  outlookToken,
  setOutlookToken,
  sharepointToken,
  setSharepointToken,
  setOutlookAccount,
  setSharepointAccount,
}) {
  const { instance } = useMsal();
  const [loginInProgress, setLoginInProgress] = useState(false);

  const handleLoginOutlook = async () => {
    if (loginInProgress) return;
    setLoginInProgress(true);
    try {
      const res = await instance.loginPopup(loginRequestOutlook);
      setOutlookToken(res.accessToken);
      setOutlookAccount(res.account);
    } finally {
      setLoginInProgress(false);
    }
  };
  const handleLoginSharePoint = async () => {
    if (loginInProgress) return;
    setLoginInProgress(true);
    try {
      const res = await instance.loginPopup(loginRequestSharePoint);
      setSharepointToken(res.accessToken);
      setSharepointAccount(res.account);
    } finally {
      setLoginInProgress(false);
    }
  };
  const handleLogout = () => {
    instance.logoutPopup();
    setOutlookToken(null);
    setSharepointToken(null);
    setOutlookAccount(null);
    setSharepointAccount(null);
  };
  return (
    <div style={{ marginBottom: 20 }}>
      <label>
        <input
          type="checkbox"
          checked={useSameAccount}
          onChange={e => setUseSameAccount(e.target.checked)}
        />
        Use the same account for both Outlook and SharePoint
      </label>
      <div style={{ marginTop: 10 }}>
        {useSameAccount ? (
          <button onClick={handleLoginOutlook} disabled={loginInProgress}>Login with Microsoft</button>
        ) : (
          <>
            <button onClick={handleLoginOutlook} style={{ marginRight: 8 }} disabled={loginInProgress}>
              Login to Outlook
            </button>
            <button onClick={handleLoginSharePoint} disabled={loginInProgress}>Login to SharePoint</button>
          </>
        )}
        <button onClick={handleLogout} style={{ marginLeft: 16 }} disabled={loginInProgress}>Logout</button>
      </div>
    </div>
  );
}

function EmailList({ token, selectedEmails, setSelectedEmails, emails }) {
  const [loading, setLoading] = useState(false);
  const [fetchedEmails, setFetchedEmails] = useState([]);

  useEffect(() => {
    if (!token) return;
    setLoading(true);
    fetch("https://graph.microsoft.com/v1.0/me/mailFolders/inbox/messages?$top=20", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        setFetchedEmails(data.value || []);
        setLoading(false);
      });
  }, [token]);

  // Use fetchedEmails if emails prop is not provided (for backward compatibility)
  const emailList = emails && emails.length > 0 ? emails : fetchedEmails;

  const toggleEmail = (id) => {
    setSelectedEmails((prev) =>
      prev.includes(id) ? prev.filter((eid) => eid !== id) : [...prev, id]
    );
  };

  return (
    <div>
      <h3>Select Emails</h3>
      {loading && <div>Loading emails...</div>}
      <ul style={{ maxHeight: 300, overflowY: "auto", padding: 0 }}>
        {emailList.map((email) => (
          <li key={email.id} style={{ listStyle: "none", marginBottom: 8 }}>
            <label>
              <input
                type="checkbox"
                checked={selectedEmails.includes(email.id)}
                onChange={() => toggleEmail(email.id)}
              />
              <b>{email.subject || "(No Subject)"}</b>  {email.from?.emailAddress?.address}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
}

function FieldMapping({ columns, fieldMapping, setFieldMapping }) {
  // Only show columns that are not hidden and are not system fields
  const selectableColumns = columns.filter(col => !col.hidden && !col.readOnly && col.name !== 'ContentType' && col.name !== 'Attachments');
  const appFields = ['subject', 'description', 'user', 'ticketnumber', 'assigned to'];
  return (
    <div style={{ margin: '20px 0', padding: 10, border: '1px solid #ccc', borderRadius: 6 }}>
      <h4>SharePoint Field Mapping</h4>
      <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
        {appFields.map(appField => (
          <label key={appField}>
            {appField.charAt(0).toUpperCase() + appField.slice(1)}:
            <select
              value={fieldMapping[appField] || ''}
              onChange={e => setFieldMapping(f => ({ ...f, [appField]: e.target.value }))}
              style={{ marginLeft: 8 }}
            >
              <option value="">(Do not map)</option>
              {selectableColumns.map(col => (
                <option key={col.name} value={col.name}>
                  {col.displayName} ({col.name})
                </option>
              ))}
            </select>
          </label>
        ))}
      </div>
    </div>
  );
}

function SharePointSelector({ token, siteId, setSiteId, listId, setListId, columns, setColumns }) {
  const [sites, setSites] = useState([]);
  const [lists, setLists] = useState([]);

  useEffect(() => {
    if (!token) return;
    fetch("https://graph.microsoft.com/v1.0/sites?search=*", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setSites(data.value || []));
  }, [token]);

  useEffect(() => {
    if (!token || !siteId) return;
    fetch(`https://graph.microsoft.com/v1.0/sites/${siteId}/lists`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setLists(data.value || []));
  }, [token, siteId]);

  useEffect(() => {
    if (!token || !siteId || !listId) {
      setColumns([]);
      return;
    }
    fetch(`https://graph.microsoft.com/v1.0/sites/${siteId}/lists/${listId}/columns`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setColumns(data.value || []));
  }, [token, siteId, listId, setColumns]);

  return (
    <div>
      <div>
        <label>
          SharePoint Site:
          <select value={siteId} onChange={e => setSiteId(e.target.value)}>
            <option value="">Select a site</option>
            {sites.map((site) => (
              <option key={site.id} value={site.id}>
                {site.displayName}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          SharePoint List:
          <select value={listId} onChange={e => setListId(e.target.value)}>
            <option value="">Select a list</option>
            {lists.map((list) => (
              <option key={list.id} value={list.id}>
                {list.displayName}
              </option>
            ))}
          </select>
        </label>
      </div>
    </div>
  );
}

function CreateTicketsButton({ token, selectedEmails, emails, siteId, listId, onResult, fieldMapping }) {
  const [loading, setLoading] = useState(false);

  const handleCreateTickets = async () => {
    if (!siteId || !listId || selectedEmails.length === 0) {
      alert("Please select emails, a site, and a list.");
      return;
    }
    setLoading(true);
    let results = [];
    for (let emailId of selectedEmails) {
      const email = emails.find((e) => e.id === emailId);
      if (!email) continue;
      // Format ticket number as YYYYMMDDHHmm from receivedDateTime (local, 24-hour/military time)
      let ticketNumber = "";
      if (email.receivedDateTime) {
        const dt = new Date(email.receivedDateTime);
        console.log('TicketNumber Debug:', email.receivedDateTime, dt.toString());
        const pad = (n) => n.toString().padStart(2, '0');
        ticketNumber = `${dt.getFullYear()}${pad(dt.getMonth()+1)}${pad(dt.getDate())}${pad(dt.getHours())}${pad(dt.getMinutes())}`;
      }
      const payload = {
        fields: {
          ...(fieldMapping.subject ? { [fieldMapping.subject]: email.subject } : {}),
          ...(fieldMapping.description ? { [fieldMapping.description]: email.bodyPreview } : {}),
          ...(fieldMapping.user ? { [fieldMapping.user]: email.from?.emailAddress?.address } : {}),
          ...(fieldMapping.ticketnumber ? { [fieldMapping.ticketnumber]: ticketNumber } : {}),
        },
      };
      console.log('SharePoint payload:', payload);
      try {
        const res = await fetch(
          `https://graph.microsoft.com/v1.0/sites/${siteId}/lists/${listId}/items`,
          {
            method: "POST",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
            body: JSON.stringify(payload),
          }
        );
        if (res.ok) {
          results.push({ emailId, status: "success" });
        } else {
          results.push({ emailId, status: "error", error: await res.text() });
        }
      } catch (err) {
        results.push({ emailId, status: "error", error: err.message });
      }
    }
    setLoading(false);
    onResult(results);
  };

  return (
    <div>
      <button onClick={handleCreateTickets} disabled={loading}>
        {loading ? "Creating..." : "Create Tickets"}
      </button>
    </div>
  );
}

function TicketSyncPage({ outlookToken, sharepointToken, useSameAccount }) {
  const [selectedEmails, setSelectedEmails] = useState([]);
  const [emails, setEmails] = useState([]);
  const [siteId, setSiteId] = useState("");
  const [listId, setListId] = useState("");
  const [columns, setColumns] = useState([]);
  const [fieldMapping, setFieldMapping] = useState({});
  const [results, setResults] = useState([]);

  // Token to use for SharePoint
  const spToken = useSameAccount ? outlookToken : sharepointToken;

  // Fetch emails after Outlook login
  useEffect(() => {
    if (!outlookToken) return;
    fetch("https://graph.microsoft.com/v1.0/me/mailFolders/inbox/messages?$top=20", {
      headers: { Authorization: `Bearer ${outlookToken}` },
    })
      .then((res) => res.json())
      .then((data) => setEmails(data.value || []));
  }, [outlookToken]);

  return (
    <div>
      <h2>Ticket Sync</h2>
      <SharePointSelector
        token={spToken}
        siteId={siteId}
        setSiteId={setSiteId}
        listId={listId}
        setListId={setListId}
        columns={columns}
        setColumns={setColumns}
      />
      {columns.length > 0 && (
        <FieldMapping
          columns={columns}
          fieldMapping={fieldMapping}
          setFieldMapping={setFieldMapping}
        />
      )}
      <EmailList
        token={outlookToken}
        selectedEmails={selectedEmails}
        setSelectedEmails={setSelectedEmails}
        emails={emails}
      />
      <CreateTicketsButton
        token={spToken}
        selectedEmails={selectedEmails}
        emails={emails}
        siteId={siteId}
        listId={listId}
        onResult={setResults}
        fieldMapping={fieldMapping}
      />
      <div style={{ marginTop: 20 }}>
        {results.length > 0 && (
          <div>
            <h4>Results:</h4>
            <ul>
              {results.map((r, i) => (
                <li key={i}>
                  Email ID: {r.emailId}  {r.status}
                  {r.error && <span style={{ color: "red" }}> ({r.error})</span>}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

function ResponseSourcesPanel({ token, selectedSites, setSelectedSites, selectedLibraries, setSelectedLibraries, selectedTeams, setSelectedTeams, selectedChannels, setSelectedChannels, additionalDocs, setAdditionalDocs }) {
  const [sites, setSites] = useState([]);
  const [libraries, setLibraries] = useState([]);
  const [teams, setTeams] = useState([]);
  const [channels, setChannels] = useState([]);
  const [loadingSites, setLoadingSites] = useState(false);
  const [loadingLibraries, setLoadingLibraries] = useState(false);
  const [loadingTeams, setLoadingTeams] = useState(false);
  const [loadingChannels, setLoadingChannels] = useState(false);
  const [selectedSiteId, setSelectedSiteId] = useState("");
  const [selectedTeamId, setSelectedTeamId] = useState("");
  const [docLink, setDocLink] = useState("");
  const [teamsError, setTeamsError] = useState("");

  useEffect(() => {
    if (!token) return;
    setLoadingSites(true);
    fetch("https://graph.microsoft.com/v1.0/sites?search=*&$top=20", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setSites(data.value || []))
      .finally(() => setLoadingSites(false));
  }, [token]);

  useEffect(() => {
    if (!token || !selectedSiteId) return;
    setLoadingLibraries(true);
    fetch(`https://graph.microsoft.com/v1.0/sites/${selectedSiteId}/drives`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setLibraries(data.value || []))
      .finally(() => setLoadingLibraries(false));
  }, [token, selectedSiteId]);

  useEffect(() => {
    if (!token) return;
    setLoadingTeams(true);
    setTeamsError("");
    fetch("https://graph.microsoft.com/v1.0/me/joinedTeams", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`Teams fetch failed: ${res.status} ${res.statusText}`);
        }
        return res.json();
      })
      .then((data) => {
        if (!data.value || data.value.length === 0) {
          setTeamsError("No Teams found. You may not be a member of any Teams, or the app may not have the required permissions (Team.ReadBasic.All, Channel.ReadBasic.All). Check your Microsoft account and app permissions.");
          setTeams([]);
        } else {
          setTeams(data.value);
        }
        console.log("Fetched Teams:", data.value);
      })
      .catch((err) => {
        setTeamsError("Failed to fetch Teams: " + err.message);
        setTeams([]);
        console.error("Teams fetch error:", err);
      })
      .finally(() => setLoadingTeams(false));
  }, [token]);

  useEffect(() => {
    if (!token || !selectedTeamId) return;
    setLoadingChannels(true);
    fetch(`https://graph.microsoft.com/v1.0/teams/${selectedTeamId}/channels`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setChannels(data.value || []))
      .finally(() => setLoadingChannels(false));
  }, [token, selectedTeamId]);

  const handleSiteChange = (e) => {
    setSelectedSiteId(e.target.value);
    setSelectedSites([e.target.value]);
    setSelectedLibraries([]);
  };
  const handleLibraryChange = (e) => {
    setSelectedLibraries([e.target.value]);
  };
  const handleTeamChange = (e) => {
    setSelectedTeamId(e.target.value);
    setSelectedTeams([e.target.value]);
    setSelectedChannels([]);
  };
  const handleChannelChange = (e) => {
    setSelectedChannels([e.target.value]);
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    setAdditionalDocs((prev) => [
      ...prev,
      ...files.map(file => ({ type: 'file', name: file.name, file }))
    ]);
    e.target.value = null;
  };

  const handleAddLink = () => {
    if (docLink.trim()) {
      setAdditionalDocs((prev) => [
        ...prev,
        { type: 'link', name: docLink, url: docLink }
      ]);
      setDocLink("");
    }
  };

  const handleRemoveDoc = (idx) => {
    setAdditionalDocs((prev) => prev.filter((_, i) => i !== idx));
  };

  return (
    <div style={{ border: '1px solid #ccc', borderRadius: 6, padding: 16, marginBottom: 20 }}>
      <h4>Response Sources</h4>
      <div style={{ marginBottom: 10 }}>
        <label><b>SharePoint Site:</b></label><br />
        {loadingSites ? (
          <span>Loading sites...</span>
        ) : (
          <select value={selectedSiteId} onChange={handleSiteChange} style={{ minWidth: 250 }}>
            <option value="">Select a site</option>
            {sites.map(site => (
              <option key={site.id} value={site.id}>{site.name || site.webUrl}</option>
            ))}
          </select>
        )}
      </div>
      {selectedSiteId && (
        <div style={{ marginBottom: 10 }}>
          <label><b>Document Library:</b></label><br />
          {loadingLibraries ? (
            <span>Loading libraries...</span>
          ) : (
            <select value={selectedLibraries[0] || ""} onChange={handleLibraryChange} style={{ minWidth: 250 }}>
              <option value="">Select a library</option>
              {libraries.map(lib => (
                <option key={lib.id} value={lib.id}>{lib.name}</option>
              ))}
            </select>
          )}
        </div>
      )}
      <div style={{ marginBottom: 10 }}>
        <label><b>Team:</b></label><br />
        {loadingTeams ? (
          <span>Loading teams...</span>
        ) : teamsError ? (
          <span style={{ color: 'red' }}>{teamsError}</span>
        ) : (
          <select value={selectedTeamId} onChange={handleTeamChange} style={{ minWidth: 250 }}>
            <option value="">Select a team</option>
            {teams.map(team => (
              <option key={team.id} value={team.id}>{team.displayName}</option>
            ))}
          </select>
        )}
      </div>
      {selectedTeamId && (
        <div style={{ marginBottom: 10 }}>
          <label><b>Channel:</b></label><br />
          {loadingChannels ? (
            <span>Loading channels...</span>
          ) : (
            <select value={selectedChannels[0] || ""} onChange={handleChannelChange} style={{ minWidth: 250 }}>
              <option value="">Select a channel</option>
              {channels.map(channel => (
                <option key={channel.id} value={channel.id}>{channel.displayName}</option>
              ))}
            </select>
          )}
        </div>
      )}
      <div style={{ marginTop: 20 }}>
        <label><b>Additional Documentation:</b></label>
        <div style={{ margin: '8px 0' }}>
          <input type="file" multiple onChange={handleFileUpload} />
        </div>
        <div style={{ margin: '8px 0' }}>
          <input
            type="text"
            value={docLink}
            onChange={e => setDocLink(e.target.value)}
            placeholder="Paste OneDrive or SharePoint link"
            style={{ minWidth: 250, marginRight: 8 }}
          />
          <button onClick={handleAddLink}>Add Link</button>
        </div>
        {additionalDocs.length > 0 && (
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            {additionalDocs.map((doc, idx) => (
              <li key={idx} style={{ marginBottom: 4 }}>
                {doc.type === 'file' ? (
                  <span> {doc.name}</span>
                ) : (
                  <span> <a href={doc.url} target="_blank" rel="noopener noreferrer">{doc.name}</a></span>
                )}
                <button onClick={() => handleRemoveDoc(idx)} style={{ marginLeft: 8, color: 'red', border: 'none', background: 'none', cursor: 'pointer' }}>Remove</button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

function ResponseGeneratorPage({ outlookToken, sharepointToken, useSameAccount }) {
  const [selectedEmails, setSelectedEmails] = useState([]);
  const [emails, setEmails] = useState([]);
  const [generatedResponse, setGeneratedResponse] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [editedResponse, setEditedResponse] = useState("");
  const [ccRecipients, setCcRecipients] = useState([]);
  const [newCcEmail, setNewCcEmail] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);
  const [selectedSites, setSelectedSites] = useState([]);
  const [selectedLibraries, setSelectedLibraries] = useState([]);
  const [selectedTeams, setSelectedTeams] = useState([]);
  const [selectedChannels, setSelectedChannels] = useState([]);
  const [additionalDocs, setAdditionalDocs] = useState([]);

  useEffect(() => {
    if (!outlookToken) return;
    fetch("https://graph.microsoft.com/v1.0/me/mailFolders/inbox/messages?$top=20", {
      headers: { Authorization: `Bearer ${outlookToken}` },
    })
      .then((res) => res.json())
      .then((data) => setEmails(data.value || []));
  }, [outlookToken]);

  const handleGenerateResponse = async () => {
    if (selectedEmails.length === 0) {
      alert("Please select at least one email to generate a response.");
      return;
    }
    setIsGenerating(true);

    // Gather the main email content (for now, just the first selected)
    const email = emails.find(e => e.id === selectedEmails[0]);
    const emailContent = email ? `${email.subject}\n\n${email.bodyPreview}` : "";

    // Prepare FormData for files and context
    const formData = new FormData();
    formData.append("emailContent", emailContent);
    formData.append("selectedSites", JSON.stringify(selectedSites));
    formData.append("selectedLibraries", JSON.stringify(selectedLibraries));
    formData.append("selectedTeams", JSON.stringify(selectedTeams));
    formData.append("selectedChannels", JSON.stringify(selectedChannels));
    formData.append("additionalDocs", JSON.stringify(additionalDocs));
    formData.append("accessToken", outlookToken); // Pass the user's Microsoft access token

    // Attach files from additionalDocs
    (additionalDocs || []).forEach(doc => {
      if (doc.type === "file" && doc.file) {
        formData.append("files", doc.file, doc.name);
      }
    });

    try {
      const response = await fetch("http://localhost:3001/generate-response", {
        method: "POST",
        body: formData,
      });
      if (!response.ok) throw new Error("Failed to generate response");
      const data = await response.json();
      setGeneratedResponse(data.response);
      setEditedResponse(data.response);
      setIsEditing(true);
    } catch (err) {
      alert("Failed to generate AI response.");
      console.error(err);
    } finally {
      setIsGenerating(false);
    }
  };

  const handleSendResponse = async () => {
    if (!editedResponse.trim()) {
      alert("Please enter a response before sending.");
      return;
    }

    console.log('Sending response:', {
      response: editedResponse,
      ccRecipients: ccRecipients,
      emailIds: selectedEmails
    });

    alert(`Response sent to ${selectedEmails.length} email(s) with ${ccRecipients.length} CC recipient(s)`);

    setGeneratedResponse("");
    setEditedResponse("");
    setCcRecipients([]);
    setIsEditing(false);
  };

  return (
    <div>
      <h2>Response Generator</h2>
      <ResponseSourcesPanel
        token={outlookToken}
        selectedSites={selectedSites}
        setSelectedSites={setSelectedSites}
        selectedLibraries={selectedLibraries}
        setSelectedLibraries={setSelectedLibraries}
        selectedTeams={selectedTeams}
        setSelectedTeams={setSelectedTeams}
        selectedChannels={selectedChannels}
        setSelectedChannels={setSelectedChannels}
        additionalDocs={additionalDocs}
        setAdditionalDocs={setAdditionalDocs}
      />
      <EmailList
        token={outlookToken}
        selectedEmails={selectedEmails}
        setSelectedEmails={setSelectedEmails}
        emails={emails}
      />
      <div style={{ margin: '20px 0', padding: 10, border: '1px solid #ccc', borderRadius: 6 }}>
        <h4>Generate Response</h4>
        {!isEditing && (
          <button onClick={handleGenerateResponse} disabled={isGenerating || selectedEmails.length === 0}>
            {isGenerating ? "Generating..." : "Generate Response"}
          </button>
        )}
        {isEditing && (
          <div>
            <div style={{ marginBottom: 10 }}>
              <label>
                <strong>Edit Response:</strong>
                <textarea
                  value={editedResponse}
                  onChange={(e) => setEditedResponse(e.target.value)}
                  style={{ width: '100%', height: '150px', marginTop: 5 }}
                  placeholder="Edit the generated response..."
                />
              </label>
            </div>
            <div style={{ marginBottom: 10 }}>
              <label>
                <strong>CC Recipients:</strong>
                <div style={{ marginTop: 5 }}>
                  {ccRecipients.map((email, index) => (
                    <span key={index} style={{ marginRight: 10, padding: '2px 8px', backgroundColor: '#f0f0f0', borderRadius: 3 }}>
                      {email}
                      <button onClick={() => setCcRecipients(ccRecipients.filter(e => e !== email))} style={{ marginLeft: 5, border: 'none', background: 'none', color: 'red', cursor: 'pointer' }}></button>
                    </span>
                  ))}
                </div>
                <div style={{ marginTop: 5 }}>
                  <input
                    type="email"
                    value={newCcEmail}
                    onChange={(e) => setNewCcEmail(e.target.value)}
                    placeholder="Enter email address"
                    style={{ marginRight: 5 }}
                  />
                  <button onClick={() => { if (newCcEmail && newCcEmail.includes("@")) { setCcRecipients([...ccRecipients, newCcEmail]); setNewCcEmail(""); } }}>Add CC</button>
                </div>
              </label>
            </div>
            <div>
              <button onClick={handleSendResponse} style={{ marginRight: 10, backgroundColor: '#4CAF50', color: 'white', border: 'none', padding: '8px 16px', borderRadius: 4 }}>
                Send Response
              </button>
              <button onClick={() => setIsEditing(false)} style={{ backgroundColor: '#f44336', color: 'white', border: 'none', padding: '8px 16px', borderRadius: 4 }}>
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

