  return (
    <nav style={{
      backgroundColor: '#f8f9fa',
      borderBottom: '1px solid #dee2e6',
      padding: '10px 0',
      marginBottom: '20px'
    }}>
      <div style={{
        maxWidth: '800px',
        margin: '0 auto',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between'
      }}>
        <div style={{ fontSize: '18px', fontWeight: 'bold', color: '#495057' }}>
          Outlook to SharePoint Tool
        </div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <button
            onClick={() => setCurrentPage('ticket-sync')}
            style={{
              padding: '8px 16px',
              backgroundColor: currentPage === 'ticket-sync' ? '#007bff' : 'transparent',
              color: currentPage === 'ticket-sync' ? 'white' : '#495057',
              border: currentPage === 'ticket-sync' ? 'none' : '1px solid #dee2e6',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: currentPage === 'ticket-sync' ? 'bold' : 'normal',
              transition: 'all 0.2s ease'
            }}
          >
            Ticket Sync
          </button>
          <button
            onClick={() => setCurrentPage('response-generator')}
            style={{
              padding: '8px 16px',
              backgroundColor: currentPage === 'response-generator' ? '#007bff' : 'transparent',
              color: currentPage === 'response-generator' ? 'white' : '#495057',
              border: currentPage === 'response-generator' ? 'none' : '1px solid #dee2e6',
              borderRadius: '4px',
              cursor: 'pointer',
              fontWeight: currentPage === 'response-generator' ? 'bold' : 'normal',
