}

function MainApp() {
  const [useSameAccount, setUseSameAccount] = useState(true);
  const [outlookToken, setOutlookToken] = useState(null);
  const [sharepointToken, setSharepointToken] = useState(null);
  const [outlookAccount, setOutlookAccount] = useState(null);
  const [sharepointAccount, setSharepointAccount] = useState(null);
  const [currentPage, setCurrentPage] = useState(() => localStorage.getItem('currentPage') || 'ticket-sync');

  useEffect(() => {
    localStorage.setItem('currentPage', currentPage);
  }, [currentPage]);

  // Session management: try to restore session on load
  useEffect(() => {
    const restoreSession = async () => {
      // Await MSAL initialization if available
      if (typeof msalInstance.initialize === 'function') {
        console.log('Awaiting msalInstance.initialize()...');
        await msalInstance.initialize();
        console.log('msalInstance initialized.');
      }
      const accounts = msalInstance.getAllAccounts();
      console.log('MSAL accounts found in cache:', accounts);
      if (accounts && accounts.length > 0) {
        const account = accounts[0];
        msalInstance.setActiveAccount(account);
        setOutlookAccount(account);
        setSharepointAccount(account);
        // Try to restore Outlook token
        try {
          const outlookRes = await msalInstance.acquireTokenSilent({ ...loginRequestOutlook, account });
          setOutlookToken(outlookRes.accessToken);
          console.log('Restored Outlook token:', outlookRes);
        } catch (e) {
          setOutlookToken(null);
          console.warn('Failed to restore Outlook token:', e);
        }
        // Try to restore SharePoint token
        try {
          const spRes = await msalInstance.acquireTokenSilent({ ...loginRequestSharePoint, account });
          setSharepointToken(spRes.accessToken);
          console.log('Restored SharePoint token:', spRes);
        } catch (e) {
          setSharepointToken(null);
          console.warn('Failed to restore SharePoint token:', e);
        }
      } else {
        console.log('No MSAL accounts found in cache');
      }
    };
    restoreSession();
  }, []);

  return (
    <div style={{ fontFamily: "sans-serif" }}>
      <NavigationMenu currentPage={currentPage} setCurrentPage={setCurrentPage} />

      <div style={{ maxWidth: 800, margin: "0 auto", padding: "0 20px" }}>
        <DualLogin
          useSameAccount={useSameAccount}
          setUseSameAccount={setUseSameAccount}
          outlookToken={outlookToken}
          setOutlookToken={setOutlookToken}
          sharepointToken={sharepointToken}
          setSharepointToken={setSharepointToken}
          setOutlookAccount={setOutlookAccount}
          setSharepointAccount={setSharepointAccount}
        />

        {(useSameAccount ? outlookToken : (outlookToken && sharepointToken)) && (
          <>
            {currentPage === 'ticket-sync' && (
              <TicketSyncPage 
                outlookToken={outlookToken}
                sharepointToken={sharepointToken}
                useSameAccount={useSameAccount}
              />
            )}

            {currentPage === 'response-generator' && (
              <ResponseGeneratorPage 
                outlookToken={outlookToken}
                sharepointToken={sharepointToken}
                useSameAccount={useSameAccount}
              />
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default function App() {
  return (
    <MsalProvider instance={msalInstance}>
      <MainApp />
    </MsalProvider>
  );
}
